import React,{ useRef,useState } from 'react';
import NodeItems from './nodeitems';
import PresentedNodes from './present_notes';
const MemHir = () => {
    const [nodes,setNodes] = useState(NodeItems);
    const inRef = useRef(0);
    const addItemToList = (event) => {
        console.log('addItemToList: '+event.keyCode);
        if(event.keyCode === 13){
            var tres = 0;
            // for(var ite=0; ite < nodes.length; ite++){
            //     if(nodes[ite] > tres)
            // }
            //{id:(nodes.length+1),name:inRef.current.value,placex:200,placey:200}
            setNodes((prevItems) => [...prevItems, {id:(nodes.length+1),name:inRef.current.value,placex:200,placey:200}]);
            inRef.current.value = "";
        }
    }
    return (<div>
        <input ref={inRef} type='text' onkeydown={addItemToList}/>
        <PresentedNodes items={nodes} />
    </div>);
}
export default MemHir;
