 import React, { useEffect, useState } from 'react';
 import { useRef } from 'react';
 var nodeitems = [{id:1,name:'ברי',placex:200,placey:200},{id:2,name:'שמא',placex:600,placey:200},{id:3,name:'חזקת ממון',placex:400,placey:200},{id:4,name:'חזקת הגוף',placex:800,placey:200},{id:5,name:'ברי חזקת ממון',placex:300,placey:(200+100*Math.sqrt(3))},{id:6,name:'שמא חזקת הגוף',placex:700,placey:(200+100*Math.sqrt(3))}];
 var architems = [{name:'ביחד',from:1,to:3},{name:'ביחד',from:2,to:4},{name:'גורר',from:1,to:5},{name:'גורר',from:3,to:5},{name:'גורר',from:2,to:6},{name:'גורר',from:4,to:6}];
 const Tooltip = (props) => {
    return (<div>
        <div style={{width:'100px',height:'100px',backgroundColor:'rgb(234, 220, 222,0.4)',top:'-104px',left:'50px',position:'absolute',borderWidth:'1px',borderColor:'grey',borderTopRightRadius:'3px',borderTopLeftRadius:'3px',borderBottomRightRadius:'3px'}}>
            <span>{props.title}</span>
            <div style={{width: 0,height: 0,borderLeft: '0px solid transparent',borderRight: '10px solid transparent', borderTop: '10px solid rgb(234, 220, 222,0.4)',position:'absolute',top:'100px',left:'0px' }}></div>
        </div>
    </div>);
 }
 const Nodemodal = (props) => {
    const rmvModal = () => {
        props.callgroup(false);
    }
    return (<div style={{position:'fixed',top:'0px',left:'0px',width:'100%',height:'100%',backgroundColor:'rgba(0,0,0,0.5)',zIndex:12}}>
        <div style={{padding:'12px',position:'absolute',left:'50px',top:'50px',zIndex:13,height:'calc(100% - 100px)',width:'calc(100% - 100px)',backgroundColor:'white',borderRadius:'10px'}}>
        <span onClick={rmvModal} style={{width:'21px',height:'24px',backgroundColor:'grey',borderRadius:'3px',position:'absolute',top:'5px',right:'5px',zIndex:14,color:'white'}}>X</span>
        </div>
    </div>);
 }
 const Roundnode = (props) => {
    var indexer = 10;
    const intervalRef = useRef(0);
    const startanim = useRef(true);
    const [blk,setBlk] = useState(0);
    
    const handleWindowLoad = ()=> {
        //window.requestAnimationFrame(changeRound);
    }
    useEffect(() => {
        window.onload = handleWindowLoad;
      }, []);
    const changeRound = ()=> {
        setBlk((a) => a+1);
        // console.log('indexer: '+indexer);
        if(indexer === 0){
            var innerCircle = intervalRef.current.getElementsByTagName('div')[0];
            var cur_width = parseInt(intervalRef.current.style.width.replace('px',''));
            if(cur_width < 60){
                intervalRef.current.style.width = (cur_width + 2)+'px';
                intervalRef.current.style.height = (cur_width + 2)+'px';
                intervalRef.current.style.top = (parseInt(intervalRef.current.style.top.replace('px','')) - 1)+'px';
                intervalRef.current.style.left = (parseInt(intervalRef.current.style.left.replace('px','')) - 1)+'px';
                // innerCircle.style.top = (parseInt(innerCircle.style.top.replace('px','')) + 1)+'px';
                // innerCircle.style.left = (parseInt(innerCircle.style.left.replace('px','')) + 1)+'px';
                innerCircle.style.width = (parseInt(innerCircle.style.width.replace('px','')) + 2)+'px';
                innerCircle.style.height = (parseInt(innerCircle.style.height.replace('px','')) + 2)+'px';

            } else {
                intervalRef.current.style.width = '40px';
                intervalRef.current.style.height = '40px';
                intervalRef.current.style.top = '10px';
                intervalRef.current.style.left = '10px';
                innerCircle.style.top = '10px';
                innerCircle.style.left = '10px';
                innerCircle.style.width = '20px';
                innerCircle.style.height = '20px';
            }
            indexer=15;
        }
        indexer--;
        if(startanim.current)
            window.requestAnimationFrame(changeRound);
        setBlk((a) => a-1);
    }
    const stopAnm = () => {
        startanim.current=false;
        console.log('onmouseout '+startanim.current);
    }
    const startChangeRound = () => {
        console.log('item_locked: '+blk);
        startanim.current=true;
        console.log('onmouseover'+startanim.current);
        changeRound();
    }
    const clearWhiteCircle = () => {
        indexer = 30;
    }
    const clmdl = () =>{
        props.callMdl(true);
    }
    return (<div onClick={clmdl} onMouseOver={startChangeRound} onMouseOut={stopAnm} title={props.title} style={{backgroundColor:'red',borderRadius:'50%',zIndex:6,position:'absolute',width:'60px',height:'60px',top:props.placey,left:props.placex}}>
        <Tooltip title={props.title} />
        <div onMouseEnter={clearWhiteCircle} ref={intervalRef} style={{backgroundColor:'white',borderRadius:'50%',zIndex:7,position:'absolute',width:'40px',height:'40px',top:'10px',left:'10px'}}>
            <div onMouseEnter={clearWhiteCircle} style={{backgroundColor:'red',borderRadius:'50%',zIndex:8,position:'absolute',width:'20px',height:'20px',top:'10px',left:'10px'}}></div>
            
        </div>
    </div>);
    
    
}
const ArchItem = (props) => {
    var arch_width,transform_deg,arch_height = 0,arch_left = 0;
    if(props.placey === props.toplacey){
        arch_width = props.placex > props.toplacex ? (props.placex - props.toplacex)+'px' : (props.toplacex - props.placex)+'px';
        transform_deg = 'rotate(0deg)';
    } else {
        var a_width = props.placex > props.toplacex ? props.placex - props.toplacex : props.toplacex - props.placex;
        var a_height = props.placey > props.toplacey ? props.placey - props.toplacey : props.toplacey - props.placey;
        arch_width = Math.sqrt(a_width*a_width + a_height*a_height);
        transform_deg = props.placex > props.toplacex ? 'rotate(-60deg)' : 'rotate(60deg)';
        arch_height = a_height/2;
        arch_left = props.placex > props.toplacex ? ((1.5*a_width)) : (a_width / 2);
    }
    
    return (<div style={{zIndex:0,top:(props.placey+30+arch_height),left:(props.placex+30 - arch_left),position:'absolute',backgroundColor:'blue',width:arch_width,transform:transform_deg,height:'2px'}}>
        <span>{props.title}</span>
    </div>);
}
const ArchGroup = () => {
    const archToNode = (from,to,md) => {
        var from_node = nodeitems[from-1];
        var to_node = nodeitems[to-1];
        switch (md){
            case 1: 
                return from_node.placex;
            break;
            case 2:
                return from_node.placey;
            break;
            case 3: 
                return to_node.placex;
            break;
            case 4: 
                return to_node.placey;
            break;
        }
    }
    return (architems.map((itm, indx) => (
        <ArchItem title={itm.name} placex={archToNode(itm.from,itm.to,1)} placey={archToNode(itm.from,itm.to,2)} toplacex={archToNode(itm.from,itm.to,3)} toplacey={archToNode(itm.from,itm.to,4)} />
      )));
}
const NodeGroup = (props) =>{
    //const itemRefs = useRef([]);
    return (nodeitems.map((item, index) => (
        <Roundnode callMdl={props.callgroup} title={item.name} placex={item.placex} placey={item.placey} />
      ))
      
    );
 }
 const Backgrnd = () =>{
    const [open_modal,setModal] = useState(false);
    return (
      <><NodeGroup callgroup={setModal} /><ArchGroup />{
        open_modal && <Nodemodal callgroup={setModal} />
        }</>
    );
 }
 export default Backgrnd;
